    public List<String> getPowerCompanyCode() {
        List<String> codes = new ArrayList();
        DataSupport.deleteAll(UserCompanyBean.class);
//        if (getEnterprise().getCompany().getHasPower() == 1) {
        getEnterprise().getCompany().save();
//        }
        if (getEnterprise().getCompanyChildren() == null || getEnterprise().getCompanyChildren().size() == 0) {
            return codes;
        }
        for (UserCompanyBean companyChildrenBean : getEnterprise().getCompanyChildren()) {
            companyChildrenBean.save();
            if (companyChildrenBean.getHasPower() == 1) {
                codes.add(companyChildrenBean.getComp_code());
            }
        }
        return codes;
    }

